pragma solidity ^0.8.24;

/**
 * @title SimpleCampaignVault  
 * @notice Simplified campaign vault without FHE for testing
 * @dev Basic campaign creation without encryption features
 */

contract SimpleCampaignVault {
    struct Campaign {
        address creator;
        uint256 total; // Plain uint256 instead of encrypted
        bool exists;
    }

    mapping(bytes32 => Campaign) private _campaigns;
    uint256 private _entered;

    event CampaignCreated(bytes32 indexed campaignId, address indexed creator);
    event DonationRecorded(bytes32 indexed campaignId, address indexed donor, uint256 amount);

    modifier nonReentrant() {
        require(_entered == 0, "REENTRANCY");
        _entered = 1;
        _;
        _entered = 0;
    }

    modifier onlyExisting(bytes32 campaignId) {
        require(_campaigns[campaignId].exists, "CAMPAIGN_NOT_FOUND");
        _;
    }

    /**
     * @notice Creates a new campaign (simplified version)
     * @param campaignId Unique identifier for the campaign
     * @param creator Address of the campaign creator
     */
    function createCampaign(bytes32 campaignId, address creator) external nonReentrant {
        require(!_campaigns[campaignId].exists, "CAMPAIGN_EXISTS");
        require(creator != address(0), "CREATOR_ZERO");

        _campaigns[campaignId] = Campaign({
            creator: creator,
            total: 0, // Initialize to 0 (plain number)
            exists: true
        });

        emit CampaignCreated(campaignId, creator);
    }

    /**
     * @notice Simple donation function for testing
     */
    function donate(bytes32 campaignId) external payable nonReentrant onlyExisting(campaignId) {
        require(msg.value > 0, "ZERO_AMOUNT");

        _campaigns[campaignId].total += msg.value;
        emit DonationRecorded(campaignId, msg.sender, msg.value);
    }

    /**
     * @notice Get campaign total (for testing)
     */
    function getCampaignTotal(bytes32 campaignId) external view onlyExisting(campaignId) returns (uint256) {
        return _campaigns[campaignId].total;
    }

    /**
     * @notice Check if campaign exists
     */
    function campaignExists(bytes32 campaignId) external view returns (bool) {
        return _campaigns[campaignId].exists;
    }

    /**
     * @notice Get campaign creator
     */
    function getCampaignCreator(bytes32 campaignId) external view onlyExisting(campaignId) returns (address) {
        return _campaigns[campaignId].creator;
    }
}
